package main

import (
	"fmt"
	"math"
)

func minWindow(s string, t string) string {
	need, window := map[string]int{}, map[string]int{}

	for _, v := range t {
		chr := string(v)
		if _, ok := need[chr]; !ok {
			need[chr] = 1
			continue
		}
		need[chr]++
	}
	
	left, right, valid := 0, 0, 0
	// 记录最小覆盖子串的起始索引及长度
	start, length := 0, math.MaxInt32
	lneed := len(s)
	
	for right < lneed {
		c := string(s[right])
		right++ //窗口右移
		//窗口内数据更新
		if _,ok := need[c];ok {
			if _, ok := window[c]; !ok {
				window[c] = 1
			}else {
				window[c]++
			}
			if need[c] == window[c] {
				valid++
			}
		}
		//判断左侧窗口是否需要更新
		for valid == len(need) {
			currLne := right - left
			if currLne < length {
				start = left
				length = currLne
			}
			d := string(s[left])
			left++

			//窗口内数据更新
			if v,ok := need[d];ok {
				if v == window[d] {
					valid--
				}
				window[d]--
			}
		}
		

	}
	fmt.Println(start,length)
	if length == math.MaxInt32 {
		return ""
	}
	return string(s[start:(start+length)])
}

func main() {

	fmt.Println(minWindow("ADOBECODEBANC","ABC"))
	fmt.Println(minWindow("a","a"))
	fmt.Println(minWindow("a","aa"))
	fmt.Println(minWindow("",""))
}